import './Chat.css';

const SERVER_URL = process.env.REACT_APP_SERVER_URL || 'http://localhost:5000';

const formatSize = (bytes) => {
  if (!bytes) return '';
  if (bytes < 1024) return bytes + ' B';
  if (bytes < 1024 * 1024) return (bytes / 1024).toFixed(1) + ' KB';
  return (bytes / (1024 * 1024)).toFixed(1) + ' MB';
};

const imageExts = ['jpg', 'jpeg', 'png', 'gif', 'webp', 'bmp', 'svg', 'tiff', 'ico', 'heic', 'heif', 'avif'];

const getFileIcon = (fileName) => {
  if (!fileName) return 'ðŸ“Ž';
  const ext = fileName.split('.').pop().toLowerCase();
  if (imageExts.includes(ext)) return 'ðŸ–¼ï¸';
  if (ext === 'pdf') return 'ðŸ“„';
  if (['zip', 'rar', '7z'].includes(ext)) return 'ðŸ—œï¸';
  if (['doc', 'docx'].includes(ext)) return 'ðŸ“';
  if (['xls', 'xlsx'].includes(ext)) return 'ðŸ“Š';
  if (['ppt', 'pptx'].includes(ext)) return 'ðŸ“ˆ';
  if (ext === 'txt' || ext === 'md') return 'ðŸ“ƒ';
  if (['mp3', 'wav', 'flac', 'aac'].includes(ext)) return 'ðŸŽµ';
  if (['mp4', 'mkv', 'avi', 'mov', 'webm'].includes(ext)) return 'ðŸŽ¬';
  return 'ðŸ“Ž';
};

const buildUrl = (url) => {
  if (!url) return null;
  if (url.startsWith('http://') || url.startsWith('https://')) return url;
  return SERVER_URL + (url.startsWith('/') ? url : '/' + url);
};

const isImageFile = (message) => {
  if (message.type === 'image') return true;
  if (message.fileUrl) {
    const ext = message.fileUrl.split('.').pop().split('?')[0].toLowerCase();
    if (imageExts.includes(ext)) return true;
  }
  if (message.fileName) {
    const ext = message.fileName.split('.').pop().toLowerCase();
    if (imageExts.includes(ext)) return true;
  }
  return false;
};

const MessageBubble = ({ message, isOwn }) => {
  const time = new Date(message.createdAt).toLocaleTimeString('fr-FR', {
    hour: '2-digit',
    minute: '2-digit',
  });

  const fileUrl  = buildUrl(message.fileUrl);
  const audioUrl = buildUrl(message.audioUrl);
  const showAsImage = isImageFile(message);

  return (
    <div className={'message-bubble ' + (isOwn ? 'own' : 'other')}>
      <div className="bubble-content">

        {message.type === 'text' && (
          <p>{message.content}</p>
        )}

        {message.type === 'voice' && audioUrl && (
          <div className="voice-message">
            <span>ðŸŽ¤</span>
            <audio src={audioUrl} controls className="audio-player" />
          </div>
        )}

        {showAsImage && fileUrl && (
          <div className="image-message">
            <a href={fileUrl} target="_blank" rel="noopener noreferrer">
              <img
                src={fileUrl}
                alt="Image"
                className="msg-image"
                onError={(e) => {
                  e.target.style.display = 'none';
                  var fallback = document.createElement('span');
                  fallback.textContent = 'ðŸ–¼ï¸ Voir image';
                  fallback.style.fontSize = '0.82rem';
                  e.target.parentNode.appendChild(fallback);
                }}
              />
            </a>
          </div>
        )}

        {!showAsImage && message.type === 'file' && fileUrl && (
          <a
            href={fileUrl}
            target="_blank"
            rel="noopener noreferrer"
            className="file-message"
            download={message.fileName}
          >
            <span className="file-icon">{getFileIcon(message.fileName)}</span>
            <div className="file-info">
              <span className="file-name">{message.fileName || 'Fichier'}</span>
              <span className="file-size">{formatSize(message.fileSize)}</span>
            </div>
            <span className="file-download">â¬‡</span>
          </a>
        )}

        {message.type !== 'text' &&
         message.type !== 'voice' &&
         !showAsImage &&
         message.type !== 'file' &&
         message.content && (
          <p>{message.content}</p>
        )}

      </div>
      <span className="message-time">{time}</span>
    </div>
  );
};

export default MessageBubble;
