import { useState, useCallback, useEffect, createContext, useContext } from 'react';
import './Toast.css';

const ToastContext = createContext();

export const ToastProvider = ({ children }) => {
  const [toasts, setToasts] = useState([]);

  const showToast = useCallback(({ title, message, type = 'info', duration = 4000 }) => {
    const id = Date.now();
    setToasts((prev) => [...prev, { id, title, message, type }]);
    setTimeout(() => {
      setToasts((prev) => prev.filter((t) => t.id !== id));
    }, duration);
  }, []);

  const removeToast = (id) => setToasts((prev) => prev.filter((t) => t.id !== id));

  return (
    <ToastContext.Provider value={{ showToast }}>
      {children}
      <div className="toast-container">
        {toasts.map((t) => (
          <div key={t.id} className={`toast toast-${t.type}`}>
            <div className="toast-icon">
              {t.type === 'success' && '‚úÖ'}
              {t.type === 'error'   && '‚ùå'}
              {t.type === 'warning' && '‚ö†Ô∏è'}
              {t.type === 'info'    && '‚ÑπÔ∏è'}
              {t.type === 'call'    && 'üìû'}
            </div>
            <div className="toast-content">
              {t.title   && <strong className="toast-title">{t.title}</strong>}
              {t.message && <span  className="toast-message">{t.message}</span>}
            </div>
            <button className="toast-close" onClick={() => removeToast(t.id)}>‚úï</button>
          </div>
        ))}
      </div>
    </ToastContext.Provider>
  );
};

export const useToast = () => useContext(ToastContext);
