import './Chat.css';

const SERVER_URL = process.env.REACT_APP_SERVER_URL || 'http://localhost:5000';

const formatSize = (bytes) => {
  if (!bytes) return '';
  if (bytes < 1024) return bytes + ' B';
  if (bytes < 1024 * 1024) return (bytes / 1024).toFixed(1) + ' KB';
  return (bytes / (1024 * 1024)).toFixed(1) + ' MB';
};

const getFileIcon = (fileName) => {
  if (!fileName) return 'ðŸ“Ž';

  const ext = fileName.split('.').pop().toLowerCase();

  // Icones pour les images
  if (['jpg', 'jpeg', 'png', 'gif', 'bmp', 'tiff', 'webp', 'svg', 'ico'].includes(ext)) {
    return 'ðŸ–¼ï¸'; // Image
  }

  // Icones pour les documents
  if (['pdf'].includes(ext)) return 'ðŸ“„';    // PDF
  if (['zip', 'rar', '7z'].includes(ext)) return 'ðŸ—œï¸';    // Archives
  if (['doc', 'docx'].includes(ext)) return 'ðŸ“';    // Word
  if (['xls', 'xlsx'].includes(ext)) return 'ðŸ“Š';    // Excel
  if (['ppt', 'pptx'].includes(ext)) return 'ðŸ“ˆ';    // PowerPoint
  if (['txt'].includes(ext)) return 'ðŸ“ƒ';    // Texte

  // Types de fichiers audio et vidÃ©o
  if (['mp3', 'wav', 'flac', 'aac'].includes(ext)) return 'ðŸŽµ';    // Audio
  if (['mp4', 'mkv', 'avi', 'mov', 'webm'].includes(ext)) return 'ðŸŽ¬';    // VidÃ©o

  // Autres types de documents
  if (['md'].includes(ext)) return 'ðŸ“‘';    // Markdown

  return 'ðŸ“Ž'; // Icone par dÃ©faut
};

const buildUrl = (url) => {
  if (!url) return null;
  if (url.startsWith('http://') || url.startsWith('https://')) return url;
  return SERVER_URL + (url.startsWith('/') ? url : '/' + url);
};

const MessageBubble = ({ message, isOwn }) => {
  const time = new Date(message.createdAt).toLocaleTimeString('fr-FR', {
    hour: '2-digit',
    minute: '2-digit',
  });

  const fileUrl  = buildUrl(message.fileUrl);
  const audioUrl = buildUrl(message.audioUrl);

  return (
    <div className={'message-bubble ' + (isOwn ? 'own' : 'other')}>
      <div className="bubble-content">

        {message.type === 'text' && (
          <p>{message.content}</p>
        )}

        {message.type === 'voice' && audioUrl && (
          <div className="voice-message">
            <span>ðŸŽ¤</span>
            <audio src={audioUrl} controls className="audio-player" />
          </div>
        )}

        {message.type === 'image' && fileUrl && (
          <div className="image-message">
            <a href={fileUrl} target="_blank" rel="noopener noreferrer">
              <img
                src={fileUrl}
                alt="Image"
                className="msg-image"
                onError={(e) => {
                  e.target.style.display = 'none';
                }}
              />
            </a>
          </div>
        )}

        {message.type === 'file' && fileUrl && (
          <a
            href={fileUrl}
            target="_blank"
            rel="noopener noreferrer"
            className="file-message"
            download={message.fileName}
          >
            <span className="file-icon">{getFileIcon(message.fileName)}</span>
            <div className="file-info">
              <span className="file-name">{message.fileName || 'Fichier'}</span>
              <span className="file-size">{formatSize(message.fileSize)}</span>
            </div>
            <span className="file-download">â¬‡</span>
          </a>
        )}

        {message.type !== 'text' &&
         message.type !== 'voice' &&
         message.type !== 'image' &&
         message.type !== 'file' &&
         message.content && (
          <p>{message.content}</p>
        )}

      </div>
      <span className="message-time">{time}</span>
    </div>
  );
};

export default MessageBubble;
